import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate, Link } from 'react-router-dom';
import '../styles/ProfilePage.css'; // Создадим отдельный файл стилей

function ProfilePage() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [apartments, setApartments] = useState([]);
  const [loading, setLoading] = useState({
    profile: true,
    apartments: true
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({
    username: '',
    full_name: '',
    phone: ''
  });
  const navigate = useNavigate();

  
  useEffect(() => {
    if (profile) {
      setEditData({
        username: profile.username || '',
        full_name: profile.full_name || '',
        phone: profile.phone || ''
      });
    }
  }, [profile]);

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditData(prev => ({
      ...prev,
      [name]: value
    }));
  };
  if (loading.profile) return <div className="loading">Загрузка профиля...</div>;

  if (!profile) return (
    <div className="error">
      Ошибка загрузки профиля. Попробуйте обновить страницу.
    </div>
  );

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const updates = {
        id: user.id,
        username: editData.username,
        full_name: editData.full_name,
        phone: editData.phone,
        updated_at: new Date().toISOString()
      };

      const { error } = await supabase
        .from('profiles')
        .upsert(updates); // Используем upsert вместо update

      if (error) throw error;
      
      setProfile(updates);
      setIsEditing(false);
      alert('Данные успешно сохранены!');
    } catch (error) {
      console.error('Ошибка обновления профиля:', error.message);
      alert('Ошибка при сохранении данных');
    }
};

  useEffect(() => {
    fetchUserData();
    fetchUserApartments();
    
    const { data: { subscription } } = supabase.auth.onAuthStateChange(() => {
      fetchUserData();
      fetchUserApartments();
    });

    return () => subscription?.unsubscribe();
  }, []);

  async function fetchUserData() {
  try {
    const { data: { user } } = await supabase.auth.getUser();
    if (!user) {
      navigate('/auth');
      return;
    }
    
    setUser(user);
    
    // Получаем или создаем профиль
    const { data, error } = await supabase
      .from('profiles')
      .select('*')
      .eq('id', user.id)
      .single();

    if (error && error.code !== 'PGRST116') throw error; // Игнорируем ошибку "не найдено"
    
    // Если профиля нет - создаем
    if (!data) {
      const { data: newProfile } = await supabase
        .from('profiles')
        .insert({
          id: user.id,
          username: user.email.split('@')[0]
        })
        .select()
        .single();
      
      setProfile(newProfile);
    } else {
      setProfile(data);
    }
  } catch (error) {
    console.error('Ошибка загрузки профиля:', error.message);
  } finally {
    setLoading(prev => ({ ...prev, profile: false }));
  }
}

  async function fetchUserApartments() {
    try {
      const { data, error } = await supabase
        .from('user_apartments')
        .select(`
          id,
          apartments (
            id,
            title,
            price,
            address,
            image_url
          ),
          favorite,
          last_viewed
        `)
        .eq('user_id', user?.id)
        .order('last_viewed', { ascending: false });
      
      if (error) throw error;
      setApartments(data || []);
    } catch (error) {
      console.error('Ошибка загрузки квартир:', error.message);
    } finally {
      setLoading(prev => ({ ...prev, apartments: false }));
    }
  }

  async function toggleFavorite(apartmentId) {
    try {
      const existing = apartments.find(a => a.apartments.id === apartmentId);
      
      if (existing) {
        const { error } = await supabase
          .from('user_apartments')
          .update({ favorite: !existing.favorite })
          .eq('id', existing.id);
        
        if (error) throw error;
        setApartments(apartments.map(a => 
          a.apartments.id === apartmentId 
            ? { ...a, favorite: !a.favorite } 
            : a
        ));
      }
    } catch (error) {
      console.error('Ошибка обновления избранного:', error.message);
    }
  }

  async function signOut() {
    await supabase.auth.signOut();
    navigate('/auth');
  }

  if (loading.profile) return <div className="loading">Загрузка профиля...</div>;

  return (
    <div className="profile-page">
      <div className="profile-header">
        <Link to="/" className="home-link">← На главную</Link>
        <h2>Мой профиль</h2>
        <button onClick={signOut} className="logout-button">
          Выйти
        </button>
      </div>
      
      <div className="profile-section">
        <div className="section-header">
          <h3>Личные данные</h3>
          {!isEditing ? (
            <button onClick={() => setIsEditing(true)} className="edit-button">
              Редактировать
            </button>
          ) : (
            <button onClick={() => setIsEditing(false)} className="cancel-button">
              Отмена
            </button>
          )}
        </div>
        
        {isEditing ? (
          <form onSubmit={handleEditSubmit} className="edit-form">
            <div className="form-group">
              <label>Имя пользователя:</label>
              <input
                type="text"
                name="username"
                value={editData.username}
                onChange={handleEditChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Полное имя:</label>
              <input
                type="text"
                name="full_name"
                value={editData.full_name}
                onChange={handleEditChange}
              />
            </div>
            <div className="form-group">
              <label>Телефон:</label>
              <input
                type="tel"
                name="phone"
                value={editData.phone}
                onChange={handleEditChange}
                pattern="[0-9]{10,15}"
                title="Номер телефона (10-15 цифр)"
              />
            </div>
            <button type="submit" className="save-button">
              Сохранить
            </button>
          </form>
        ) : (
          <div className="profile-info">
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Имя пользователя:</strong> {profile?.username || 'Не указано'}</p>
            <p><strong>Полное имя:</strong> {profile?.full_name || 'Не указано'}</p>
            <p><strong>Телефон:</strong> {profile?.phone || 'Не указано'}</p>
          </div>
        )}
      </div>
      
      <div className="apartments-section">
        <h3>Мои квартиры</h3>
        {loading.apartments ? (
          <div className="loading">Загрузка списка квартир...</div>
        ) : apartments.length === 0 ? (
          <p className="no-apartments">Вы пока не просматривали квартиры</p>
        ) : (
          <div className="apartments-grid">
            {apartments.map(({ apartments: apt, favorite }) => (
              <div key={apt.id} className="apartment-card">
                <img 
                  src={apt.image_url || '/default-apartment.jpg'} 
                  alt={apt.title} 
                  className="apartment-image"
                />
                <div className="apartment-info">
                  <h4>{apt.title}</h4>
                  <p>{apt.address}</p>
                  <p className="price">{apt.price.toLocaleString()} ₽</p>
                </div>
                <button 
                  onClick={() => toggleFavorite(apt.id)}
                  className={`favorite-button ${favorite ? 'active' : ''}`}
                >
                  {favorite ? '★' : '☆'}
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default ProfilePage;
