import './contact.css';
import Footer from '../layouts/Footer';
import SmallHeader from '../layouts/SmallHeader';
import { Link, useNavigate, useLocation } from 'react-router-dom'; // Import useLocation
import { useState, useEffect } from 'react';

export default function Contacts() {
  const navigate = useNavigate();

  const handleSubmit = () => {
    // Use URLSearchParams to pass the notification state
    const params = new URLSearchParams();
    params.append('showNotification', 'true'); // Pass the notification flag

    navigate(`/?${params.toString()}`); // Navigate with the query parameter

  };

  return (
    <>
      <SmallHeader />
      <div className="forma">
        <div className="form-at">
          <div className="validate-input-at w-50" data-validate="Обязательное поле">
            <input
              className="input-at"
              type="text"
              name="name-at"
              placeholder="Ваше имя"
            />
            <span className="focus-input-at" />
          </div>
          <div className="validate-input-at w-50" data-validate="Обязательное поле">
            <input
              className="input-at"
              type="text"
              name="email-at"
              placeholder="Ваш телефон или email"
            />
            <span className="focus-input-at" />
          </div>
          <div className="validate-input-at" data-validate="Обязательное поле">
            <textarea
              className="input-at"
              name="message-at"
              placeholder="Ваше сообщение"
              defaultValue={''}
            />
            <span className="focus-input-at" />
          </div>
          <input
            defaultChecked={true}
            className="input-at"
            id="checkbox-at"
            type="checkbox"
            name="checkbox-at"
            onChange={(e) => {
              document.getElementById('submit-at').disabled = !e.target.checked;
            }}
          />
          <label htmlFor="checkbox-at">
            Настоящим подтверждаю, что я ознакомлен и согласен с{' '}
            <Link to="/agreement">пользовательским соглашением</Link>
          </label>
          <input type="hidden" name="subject-at" defaultValue="Тема формы" />
          <button
            id="submit-at"
            className="form-at-btn"
            onClick={handleSubmit}
            disabled={false} // Enabled by default
          >
            Отправить
          </button>
        </div>
        <div className="result-at" />
      </div>
      <Footer />
    </>
  );
}


// --- Home Page or Component Where You Want to Show the Notification ---
function HomePage() {  // Or whatever component renders on route "/"
  const location = useLocation();
  const [showNotification, setShowNotification] = useState(false);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    if (params.get('showNotification') === 'true') {
      setShowNotification(true);

      const timer = setTimeout(() => {
        setShowNotification(false);
      }, 3000);

      return () => clearTimeout(timer);
    }
  }, [location.search]);  // Important:  Depend on location.search


  return (
    <>
      {showNotification && (
        <div className="fixed top-5 left-1/2 transform -translate-x-1/2 bg-green-500 text-white p-4 rounded-md shadow-lg z-50">
          Заявка успешно отправлена!
        </div>
      )}
      {/* Rest of your home page content here */}
      <h1>Welcome to the Home Page</h1>
    </>
  );
}
