import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import SmallHeader from '../layouts/SmallHeader';

const apartmentsData = [
  {
    id: 1,
    title: "Пентхаус Utopia House",
    src: "/img/cards/01.jpg",
    price: 10000000,
    has_pool: false,
    rooms: 5,
    has_service: false,
    description: "Роскошный пентхаус с панорамным видом"
  },
  {
    id: 2,
    title: "Апартаменты NiceLoft",
    src: "/img/cards/02.jpg",
    price: 20000000,
    has_pool: false,
    rooms: 6,
    has_service: true,
    description: "Современные апартаменты с обслуживанием"
  },
  {
    id: 3,
    title: "Апартаменты LoftStudio",
    src: "/img/cards/03.jpg", 
    price: 30000000,
    has_pool: true,
    rooms: 7,
    has_service: false,
    description: "Студия премиум-класса с бассейном"
  },
  {
    id: 4,
    title: "Loft Квартира Престиж",
    src: "/img/cards/04.jpg",
    price: 40000000,
    has_pool: true,
    rooms: 8,
    has_service: true,
    description: "Элитная квартира с полным сервисом"
  }
];

export default function Catalog() {
  const [selectedApartment, setSelectedApartment] = useState(null);
  const [userApartments, setUserApartments] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    checkUser();
  }, []);

  useEffect(() => {
    if (user) {
      fetchUserApartments();
    }
  }, [user]);

  async function checkUser() {
    const { data: { user } } = await supabase.auth.getUser();
    setUser(user);
  }

  async function fetchUserApartments() {
    const { data, error } = await supabase
      .from('user_apartments')
      .select('apartment_id')
      .eq('user_id', user.id);
    
    if (!error && data) {
      setUserApartments(data.map(item => item.apartment_id));
    }
  }

  async function handleSelectApartment(apartmentId) {
    if (!user) {
      alert('Для выбора квартир необходимо авторизоваться');
      return;
    }

    try {
      // Проверяем, есть ли уже эта квартира у пользователя
      const isSelected = userApartments.includes(apartmentId);
      
      if (isSelected) {
        // Удаляем из выбранных
        const { error } = await supabase
          .from('user_apartments')
          .delete()
          .eq('user_id', user.id)
          .eq('apartment_id', apartmentId);
        
        if (!error) {
          setUserApartments(prev => prev.filter(id => id !== apartmentId));
        }
      } else {
        // Добавляем в выбранные
        const { error } = await supabase
          .from('user_apartments')
          .insert({
            user_id: user.id,
            apartment_id: apartmentId,
            selected_at: new Date().toISOString()
          });
        
        if (!error) {
          setUserApartments(prev => [...prev, apartmentId]);
        }
      }
    } catch (error) {
      console.error('Ошибка при обновлении выбора:', error);
    }
  }

  return (
    <>
      <SmallHeader />
      <div className="container">
        <div className="apartments__title">
          <h2 className="title-2 text-center m-11">Наши квартиры</h2>
        </div>
        <div className="apartments__cards">
          {apartmentsData.map(apartment => (
            <ApartmentCard
              key={apartment.id}
              apartment={apartment}
              isSelected={userApartments.includes(apartment.id)}
              onSelect={handleSelectApartment}
              onDetailsClick={setSelectedApartment}
            />
          ))}
        </div>
      </div>

      {selectedApartment && (
        <ApartmentModal
          apartment={selectedApartment}
          onClose={() => setSelectedApartment(null)}
          isSelected={userApartments.includes(selectedApartment.id)}
          onSelect={handleSelectApartment}
        />
      )}

      <Footer />
    </>
  );
}

function ApartmentCard({ apartment, isSelected, onSelect, onDetailsClick }) {
  return (
    <div className={`card ${isSelected ? 'selected' : ''}`}>
      <img 
        className="card__img" 
        src={apartment.src} 
        alt={apartment.title} 
      />
      <div className="card__content">
        <h3 className="card__title">{apartment.title}</h3>
        <p className="card__price">{apartment.price.toLocaleString()} ₽</p>
        
        <div className="card__actions">
          <button 
            className="btn-details"
            onClick={() => onDetailsClick(apartment)}
          >
            Подробнее
          </button>
          <button
            className={`btn-select ${isSelected ? 'selected' : ''}`}
            onClick={() => onSelect(apartment.id)}
          >
            {isSelected ? '✓ В избранном' : 'В избранное'}
          </button>
        </div>
      </div>
    </div>
  );
}

function ApartmentModal({ apartment, onClose, isSelected, onSelect }) {
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>×</button>
        
        <div className="modal-grid">
          <div className="modal-image">
            <img src={apartment.src} alt={apartment.title} />
          </div>
          
          <div className="modal-info">
            <h3>{apartment.title}</h3>
            <p className="modal-price">{apartment.price.toLocaleString()} ₽</p>
            <p className="modal-description">{apartment.description}</p>
            
            <div className="modal-features">
              <p>Комнат: {apartment.rooms}</p>
              <p>Бассейн: {apartment.has_pool ? '✓' : '✗'}</p>
              <p>Обслуживание: {apartment.has_service ? '✓' : '✗'}</p>
            </div>
            
            <button
              className={`modal-select-btn ${isSelected ? 'selected' : ''}`}
              onClick={() => {
                onSelect(apartment.id);
                onClose();
              }}
            >
              {isSelected ? 'Удалить из избранного' : 'Добавить в избранное'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

function Footer() {
  return (
    <footer className="footer">
      {/* ... ваш существующий код футера ... */}
    </footer>
  );
}
