import './contact.css';
import Footer from '../layouts/Footer';
import SmallHeader from '../layouts/SmallHeader';
import { Link, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react'; // Import useState and useEffect

export default function Contacts() {
  const navigate = useNavigate();
  const [showNotification, setShowNotification] = useState(false);

  useEffect(() => {
    if (showNotification) {
      const timer = setTimeout(() => {
        setShowNotification(false);
      }, 3000); // Уведомление будет видно в течение 3 секунд

      return () => clearTimeout(timer); // Очищаем таймер при размонтировании компонента
    }
  }, [showNotification]);

  const handleSubmit = () => {
    setShowNotification(true);
    navigate('/'); // Переход на главную страницу
  };

  return (
    <>
      <SmallHeader />
      <div className="forma">
        <div className="form-at">
          <div className="validate-input-at w-50" data-validate="Обязательное поле">
            <input
              className="input-at"
              type="text"
              name="name-at"
              placeholder="Ваше имя"
            />
            <span className="focus-input-at" />
          </div>
          <div className="validate-input-at w-50" data-validate="Обязательное поле">
            <input
              className="input-at"
              type="text"
              name="email-at"
              placeholder="Ваш телефон или email"
            />
            <span className="focus-input-at" />
          </div>
          <div className="validate-input-at" data-validate="Обязательное поле">
            <textarea
              className="input-at"
              name="message-at"
              placeholder="Ваше сообщение"
              defaultValue={''}
            />
            <span className="focus-input-at" />
          </div>
          <input
            defaultChecked={true}
            className="input-at"
            id="checkbox-at"
            type="checkbox"
            name="checkbox-at"
            onChange={(e) => {
              document.getElementById('submit-at').disabled = !e.target.checked;
            }}
          />
          <label htmlFor="checkbox-at">
            Настоящим подтверждаю, что я ознакомлен и согласен с{' '}
            <Link to="/agreement">пользовательским соглашением</Link>
          </label>
          <input type="hidden" name="subject-at" defaultValue="Тема формы" />
          <button
            id="submit-at"
            className="form-at-btn"
            onClick={handleSubmit}
            disabled={false} // Enabled by default
          >
            Отправить
          </button>
        </div>
        <div className="result-at" />
      </div>
      {showNotification && (
        <div className="fixed top-5 left-1/2 transform -translate-x-1/2 bg-green-500 text-white p-4 rounded-md shadow-lg z-50">
          Заявка успешно отправлена!
        </div>
      )}
      <Footer />
    </>
  );
}
