import React, { useEffect, useState, useCallback } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate, Link } from 'react-router-dom';
import '../styles/ProfilePage.css';

function ProfilePage() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [favoriteApartments, setFavoriteApartments] = useState([]);
  const [loading, setLoading] = useState({
    profile: true,
    apartments: true
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({
    username: '',
    full_name: '',
    phone: ''
  });
  const navigate = useNavigate();

  // Получение данных пользователя
  const fetchUserData = useCallback(async () => {
    try {
      setLoading(prev => ({ ...prev, profile: true }));
      
      const { data: { user }, error: authError } = await supabase.auth.getUser();
      if (authError || !user) {
        navigate('/auth');
        return;
      }

      setUser(user);

      const { data: profileData, error: profileError } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', user.id)
        .single();

      if (profileError && profileError.code !== 'PGRST116') throw profileError;

      if (profileData) {
        setProfile(profileData);
        setEditData({
          username: profileData.username || '',
          full_name: profileData.full_name || '',
          phone: profileData.phone || ''
        });
      }
    } catch (error) {
      console.error('Ошибка загрузки профиля:', error);
    } finally {
      setLoading(prev => ({ ...prev, profile: false }));
    }
  }, [navigate]);

  // Получение избранных квартир (исправленная версия)
  const fetchFavoriteApartments = useCallback(async () => {
    if (!user?.id) return;

    try {
      setLoading(prev => ({ ...prev, apartments: true }));
      
      // 1. Сначала получаем ID избранных квартир
      const { data: favoritesData, error: favoritesError } = await supabase
        .from('user_favorites')
        .select('apartment_id')
        .eq('user_id', user.id);

      if (favoritesError) throw favoritesError;
      if (!favoritesData || favoritesData.length === 0) {
        setFavoriteApartments([]);
        return;
      }

      const apartmentIds = favoritesData.map(fav => fav.apartment_id);

      // 2. Затем получаем полные данные по этим квартирам
      const { data: apartmentsData, error: apartmentsError } = await supabase
        .from('apartments')
        .select('*')
        .in('id', apartmentIds);

      if (apartmentsError) throw apartmentsError;
      
      setFavoriteApartments(apartmentsData || []);
    } catch (error) {
      console.error('Ошибка загрузки избранных квартир:', error);
    } finally {
      setLoading(prev => ({ ...prev, apartments: false }));
    }
  }, [user]);

  // Подписка на изменения аутентификации
  useEffect(() => {
    const { data: { subscription } } = supabase.auth.onAuthStateChange((event) => {
      if (event === 'SIGNED_IN') {
        fetchUserData();
      } else if (event === 'SIGNED_OUT') {
        navigate('/auth');
      }
    });

    return () => subscription?.unsubscribe();
  }, [fetchUserData, navigate]);

  // Загрузка данных при монтировании
  useEffect(() => {
    fetchUserData();
  }, [fetchUserData]);

  // Загрузка избранных квартир при изменении пользователя
  useEffect(() => {
    if (user) {
      fetchFavoriteApartments();
    }
  }, [user, fetchFavoriteApartments]);

  // Остальные функции (handleEditChange, handleEditSubmit, signOut) остаются без изменений

  const toggleFavorite = async (apartmentId) => {
    try {
      const { error } = await supabase
        .from('user_favorites')
        .delete()
        .eq('user_id', user.id)
        .eq('apartment_id', apartmentId);
      
      if (error) throw error;
      
      setFavoriteApartments(prev => 
        prev.filter(apartment => apartment.id !== apartmentId)
      );
    } catch (error) {
      console.error('Ошибка при удалении из избранного:', error);
    }
  };

  return (
    <div className="profile-page">
      {/* ... (остальная часть JSX остается без изменений) ... */}
      
      <div className="apartments-section">
        <h3>Мои квартиры</h3>
        {loading.apartments ? (
          <div className="loading">Загрузка списка квартир...</div>
        ) : favoriteApartments.length === 0 ? (
          <p className="no-apartments">Вы пока не добавили квартиры в избранное</p>
        ) : (
          <div className="apartments-grid">
            {favoriteApartments.map(apartment => (
              <div key={apartment.id} className="apartment-card">
                <img 
                  src={apartment.image_url || '/default-apartment.jpg'} 
                  alt={apartment.title}
                  className="apartment-image"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = '/default-apartment.jpg';
                  }}
                />
                <div className="apartment-info">
                  <h4>{apartment.title}</h4>
                  <p className="price">{apartment.price.toLocaleString()} ₽</p>
                  <p>Комнат: {apartment.rooms}</p>
                </div>
                <button 
                  onClick={() => toggleFavorite(apartment.id)}
                  className="favorite-button active"
                >
                  ★ Удалить
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default ProfilePage;
